(22, 'Calciu D''glucarate', '', 'ANTI-CARCINOGEN, ANTI-TUMORAL, ANTI-METASTATIC, DETOXIFIANT', 81.26, 3, 0, 23, 0, 'nu', 'nu'),
(72, 'STEMULIN- Activator Celule Stem 90c', '', 'STEMULINTM \r\n\r\nACTIVATOR DE CELULE STEM \r\n\r\nSTEM CELL ACTIVATOR  \r\n\r\nO formulatie Canadiana Originala \r\n\r\nSTEMULIN: o alternativa naturala pentru transplantul de celule stem \r\n\r\n90 CAPSULE \r\nFabricat in Canada conf. GMP si ISO 9001\r\nTRATAMENTELE CU CELULE STEM REPREZINTA ULTIMA REVOLUTIE IN MEDICINA MODERNA \r\n\r\nIn momentul de fata  exista indicatii specifice de utilizare a celulelor stem in peste 70 de boli\r\nREFACEREA ORGANELOR BOLNAVE, DEGENERATE, INBATRANITE \r\n\r\nRESTAURAREA BALANTEI BIO-CHIMICE \r\n\r\nMARIREA TRAFICULUI LIMFOCITELOR SI MONOCITELOR', 175.46, 6, 0, 24, 0, 'nu', 'nu'),
(84, 'Acid ALFA Lipoic (R-ALA) 100 mg/60 cps', '', 'ACID ALFA LIPOIC (R-ALA) \r\n\r\nANTIOXIDANT METABOLIC \r\n\r\nExtras 100% din surse Naturale \r\n\r\n100% Standardizat  \r\n\r\n100 mg /60 capsule\r\n\r\nAntioxidant solubil in grasimi \r\n\r\n\r\nFoarte eficient in tratare Diabet Zaharat (hiperglicemie)\r\nPuternic Detoxifiant pt. Ficat, Hepatoprotector (Steatoza, Hepatita, Ficat Obosit)\r\nRecicleaza ceilalti Antioxidanti', 68.19, 81, 0, 23, 0, 'nu', 'nu'),
(85, 'ACID ALFA LIPOIC Forte 250mg/60 caps', '', 'ACID ALFA LIPOIC - Forte (R-ALA) \r\n\r\n250 mg/60 capsule \r\n\r\n100% Pur\r\n\r\n100% Standardizat\r\n\r\nUn produs unic, superior de la Organika\r\nPoarta stampilele: QCM si ISO 9001', 97.53, 81, 0, 23, 0, 'nu', 'nu'),
